    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
       double valor = Double.parseDouble(txtValor.getText());
       double juros = Double.parseDouble(txtJuros.getText());
       int meses = Integer.parseInt(txtMeses.getText());
      
       
        juros = juros/100;
        
        //inicia resultado com 0
        double resultado, total ;
        resultado = 0;
        total= 0;
        //faz o la√ßo calculando o valor
        for (int tempo1=1; tempo1<=meses; ++tempo1){
        total = (valor*juros);
        resultado = total + valor;
        }
        String retorno = String.format("%.2f",resultado );
        lblResultado.setText(retorno);
        
    }//GEN-LAST:event_btnCalcularActionPerformed

